set(&apos;/item/aaa/hIssValLength&apos;,hIssValStr.length());\n*/\nif (hIssType = &apos;date&apos; &amp;&amp; hIssValStr.length()==19)\n{\n  hIssValDay=hIssValStr.substr(8,2);\n  hIssValMonth=hIssValStr.substr(5,2);\n  if (hIssValMonth==&quot;01&quot;) {hIssValMonth=&quot;January&quot;};\n  if (hIssValMonth==&quot;02&quot;) {hIssValMonth=&quot;February&quot;};\n  if (hIssValMonth==&quot;03&quot;) {hIssValMonth=&quot;March&quot;};\n  if (hIssValMonth==&quot;04&quot;) {hIssValMonth=&quot;April&quot;};\n  if (hIssValMonth==&quot;05&quot;) {hIssValMonth=&quot;May&quot;};\n  if (hIssValMonth==&quot;06&quot;) {hIssValMonth=&quot;June&quot;};\n  if (hIssValMonth==&quot;07&quot;) {hIssValMonth=&quot;July&quot;};\n  if (hIssValMonth==&quot;08&quot;) {hIssValMonth=&quot;August&quot;};\n  if (hIssValMonth==&quot;09&quot;) {hIssValMonth=&quot;September&quot;};\n  if (hIssValMonth==&quot;10&quot;) {hIssValMonth=&quot;October&quot;};\n  if (hIssValMonth==&quot;11&quot;) {hIssValMonth=&quot;November&quot;};\n  if (hIssValMonth==&quot;12&quot;) {hIssValMonth=&quot;December&quot;};\n\n  /* testing - uncomment to show variables in XML\n  xml.set(&apos;/item/aaa/hIssValDay&apos;,hIssValDay);\n  xml.set(&apos;/item/aaa/hIssValMonth&apos;,hIssValMonth);\n  */\n  hTitle=hTitle+&apos;, &apos;+hIssValDay+&apos; &apos;+hIssValMonth;\n}\nelse\n{\n  /* if a value exists, check to see if it is actually a number before adding &quot;no.&quot; */\n  if (hIssVal != &apos;&apos;)\n  {\n    if (isNaN(hIssVal))\n    {\n      hTitle=hTitle+&apos;, &apos;+hIssVal;\n    }\n    else\n    {\n      hTitle=hTitle+&apos;, no. &apos;+hIssVal;\n    }\n  }\n}\n\n/* Finally check for and add year to the display title */\nif (hYear != &apos;&apos;) {hTitle=hTitle+&apos;, &apos;+hYear}\n\n/* Store the created value back to the display title */\nxml.set(&apos;/item/itembody/name&apos;,hTitle);</saveScript>\n  <allowNonSequentialNavigation>false</allowNonSequentialNavigation>\n  <showPageTitlesNextPrev>false</showPa