ments.list();\n\tfor (var i=0; i &lt; attachList.size(); i++)\n\t{\n\t\tvar attach = attachList.get(i);\n\n\t\t//the filename of the attachment (e.g. myfile.jpg)\n\t\tvar filename = attach.getFilename();\n\n\t\t//get the MIME type for this attachment\n\t\tvar fileMimeType = mime.getMimeTypeForFilename(filename);\n\n\t\tif (fileMimeType != null)\n\t\t{\n\t\t\tvar mimeType = fileMimeType.getType();\n\t\t\t//is it an image type?\n\t\t\tif (mimeType.match(&apos;image/&apos;))\n\t\t\t{\n\t\t\t\t//get the size of the image\n\t\t\t\tvar dimensions = images.getDimensions(filename);\n\t\t\t\tvar width = dimensions.getWidth();\n\t\t\t\tvar height = dimensions.getHeight();\n\t\n\t\t\t\t//create a new temporary image of half the size\n\t\t\t\timages.resize(filename, width/2, height/2, &apos;TEMPIMAGE&apos;);\n\t\n\t\t\t\t//delete the original image\n\t\t\t\tstaging.deleteFile(filename);\n\t\n\t\t\t\t//rename the temporary image (back to the original filename)\n\t\t\t\tstaging.move(&apos;TEMPIMAGE&apos;, filename);\n\t\n\t\t\t\t//add information to the XML so we can provide feedback to the user\n\t\t\t\tvar resizeInfo = xml.createSubtree(&apos;resized&apos;);\n\t\t\t\tresizeInfo.set(&apos;attachmentName&apos;, filename);\n\t\t\t\tresizeInfo.set(&apos;fromWidth&apos;, width);\n\t\t\t\tresizeInfo.set(&apos;fromHeight&apos;, height);\n\t\t\t\tresizeInfo.set(&apos;toWidth&apos;, width/2);\n\t\t\t\tresizeInfo.set(&apos;toHeight&apos;, height/2);\n\t\t\t}\n\t\t}\n\t}\n}\n\n//detect if the Resize Images! button was pressed\nif (request.get(&apos;resizeImages&apos;) != null)\n{\n\tresizeImages();\n}</string>\n            </entry>\n            <entry>\n              <string>ReadJs</string>\n              <string>//list all the current attachments and loop through them\nattributes.set(&apos;showresize&apos;,&apos;false&apos;);\nvar attachList = attachments.list();\nfor (var i=0; i &lt; attachList.size(); i++)\n{\n\tvar attach = attachList.get(i);\n\n\t//the filename of the attachment (e.g. myfile.jpg)\n\tvar filename = attach.getFilename();\n\n\t//get the MIME type for this attachment\n\tvar fileMimeType = mime.getMimeTypeForFilename(filename);\n\tif(fileMimeType != null)\n\t{\n\t\tvar mimeType = fileMimeType.getType();\n\t\n\t\t// if there is at least 1 image show the resize image control?\n\t\tif (mimeType.match(&apos;image/&apos;))\n\t\t{\n\t\t\tattributes.set(&apos;showresize&apos;,&apos;true&apos;);\n\t\t\tbreak;\n\t\t}\n\t}\n}</string>\n            </entry>\n            <entry>\n              <string>JsLibs</string>\n              <list/>\n            </entry>\n            <entry>\n              <string>OnSubmitJs</string>\n              <string>return true;</string>\n            </entry>\n            <entry>\n              <string>MarkUp</string>\n              <string>&lt;#if attributes.get(&apos;showresize&apos;) == &quot;true&quot; &gt;\n\t&lt;div class=&quot;ctrlbody&quot;&gt;\n\n\t&lt;#-- render a button to force resizing of images --&gt;\n\t&lt;input id=&quot;${prefix}resizeImages&quot; name=&quot;${prefix}resizeImages&quot; type=&quot;submit&quot; value=&quot;Resize Images!&quot; onclick=&quot;${submitJavascript}&quot; style=&quot;background-color:#FF0000; color: #FFFFFF; width:200px; height:60px;&quot;&gt;\n\tHalves the width and height of all image attachments on this item.\n\t\n\t&lt;#-- list all the resized images since the last page submission --&gt;\n\t&lt;#list xml.getAllSubtrees(&apos;resized&apos;) as resized&gt;\n\t\t&lt;#-- previewUrlBase in final release only --&gt;\n\t\t&lt;img src=&quot;${previewUrlBase}${resized.get(&apos;attachmentName&apos;)}&quot; alt=&quot;${resized.get(&apos;attachmentName&apos;)}&quot;&gt;\n\t\n\t\t&lt;h3&gt;Attachment ${resized.get(&apos;attachmentName&apos;)} was resized&lt;/h3&gt;\n\t\t&lt;br&gt;Resized From: ${resized.get(&apos;fromWidth&apos;)} x ${resized.get(&apos;fromHeight&apos;)}\n\t\t&lt;br&gt;Resized To: ${resized.get(&apos;toWidth&apos;)} x ${resized.get(&apos;toHeight&apos;)}\n\t&lt;/