com.dytech.edge.wizard.beans.control.Edi.script:
var bRet = false; 
if( user.hasRole('ROLE_CONTENT_ADMINISTRATOR') ) 
{ 
    bRet = true; 
} 
return bRet;

var bRet = false; 
if( xml.get('/item/copyright/issue/type') != 'date' ) 
{ 
    bRet = true; 
} 
return bRet;

com.dytech.edge.wizard.beans.control.Cal.script:
var bRet = false; 
if( xml.contains('/item/copyright/issue/type', 'date') ) 
{ 
    bRet = true; 
} 
return bRet;

wizard.saveScript:
/* save script to modify the item display title to be of the form:
<title>[, v.<vol>][, <issuevalue>][, <year>] 
where issuevalue is one of:
no. <issuevalue> (if a number)
dd mmmmmmm (if a date), or
<issuevalue> 							*/

var hTitle = xml.get('/item/copyright/title');
var hVol = xml.get('/item/copyright/volume');
var hIssType = xml.get('/item/copyright/issue/type');
var hIssVal = xml.get('/item/copyright/issue/value');
var hYear = xml.get('/item/copyright/publication/year');

/* testing - uncomment to show variables in XML
xml.set('/item/aaa/hTitle',hTitle);
xml.set('/item/aaa/hVol',hVol);
xml.set('/item/aaa/hIssType',hIssType);
xml.set('/item/aaa/hIssVal',hIssVal);
xml.set('/item/aaa/hYear',hYear);
 */

/* Add volume if it exists */
if (hVol != '') {hTitle=hTitle+', vol. '+hVol}

/* change IssVal from <date>T<time> to dd mmmmmmm */
var hIssValStr = hIssVal.toString();

/* testing - uncomment to show variables in XML
xml.set('/item/aaa/hIssValStr',hIssValStr);
xml.set('/item/aaa/hIssValLength',hIssValStr.length());
*/
if (hIssType = 'date' && hIssValStr.length()==19)
{
  hIssValDay=hIssValStr.substr(8,2);
  hIssValMonth=hIssValStr.substr(5,2);
  if (hIssValMonth=="01") {hIssValMonth="January"};
  if (hIssValMonth=="02") {hIssValMonth="February"};
  if (hIssValMonth=="03") {hIssValMonth="March"};
  if (hIssValMonth=="04") {hIssValMonth="April"};
  if (hIssValMonth=="05") {hIssValMonth="May"};
  if (hIssValMonth=="06") {hIssValMonth="June"};
  if (hIssValMonth=="07") {hIssValMonth="July"};
  if (hIssValMonth=="08") {hIssValMonth="August"};
  if (hIssValMonth=="09") {hIssValMonth="September"};
  if (hIssValMonth=="10") {hIssValMonth="October"};
  if (hIssValMonth=="11") {hIssValMonth="November"};
  if (hIssValMonth=="12") {hIssValMonth="December"};

  /* testing - uncomment to show variables in XML
  xml.set('/item/aaa/hIssValDay',hIssValDay);
  xml.set('/item/aaa/hIssValMonth',hIssValMonth);
  */
  hTitle=hTitle+', '+hIssValDay+' '+hIssValMonth;
}
else
{
  /* if a value exists, check to see if it is actually a number before adding "no." */
  if (hIssVal != '')
  {
    if (isNaN(hIssVal))
    {
      hTitle=hTitle+', '+hIssVal;
    }
    else
    {
      hTitle=hTitle+', no. '+hIssVal;
    }
  }
}

/* Finally check for and add year to the display title */
if (hYear != '') {hTitle=hTitle+', '+hYear}

/* Store the created value back to the display title */
xml.set('/item/itembody/name',hTitle);
