wizard.saveScript:
/* add save script to add status = copyright for any portion where status is blank */

var prop = xml.getPropBagEx();
var iter = prop.iterateAll('/item/copyright/portions/portion/sections/section/');
for(var i=0;iter.hasNext();i++)
{
    var xt = iter.next();
    var thisStatus = xt.getNode('copyrightstatus');
    if (thisStatus=='')
    {
      xt.setNode('copyrightstatus','copyright');
    }
}
